mapper.update:
update user
set `name` = #{name,jdbcType=VARCHAR},
age = #{age,jdbcType=INTEGER},
balance = #{balance,jdbcType=DECIMAL},
data_base = #{dataBase,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, `name`, age, balance, data_base
from user
where id = #{id,jdbcType=BIGINT}
